import React, { useState } from 'react'
import SkillCard from '../components/SkillCard';

import data from '../components/data/data.json';

const Projects = () => {

	const [currentProject, setCurrentProject] = useState(1)

	if(currentProject === data.projects.length + 1) {
		setCurrentProject(1)
	}

	console.log('render')

	return (
		<section className="projects">
			{
				data.projects.map((project, index) => {
					const { name, link, skills, image, desc } = project

					let className = ""

					if(index + 1 === currentProject) {
						className = "currentProject"
					} else if(index + 2 === currentProject) {
						className = "lastProject"
					} else {
						className = "nextProject"
					}

					if(currentProject === 1) {
						if(index + 1 === data.projects.length) {
							className = "lastProject"
						}
					}

					return (
						<div className={`container project-card`} key={index}>
							<div className={`${className}`}>
								<h2>{name}</h2>
								<div className="project-hero">
									<img src={image} alt={name} />
									<div>
										<h4>Skills Used</h4>
										<div className="project-skill-cards">
										{
											skills.map((skill, index) => {
												return (
													<SkillCard key={index} skillName={skill}/>
												)
											})
										}
										</div>
									</div>
								</div>
								<div className="project-desc">
									{
										desc.map((description, index) => {
											return (
												<p key={index}>{description}</p>
											)
										})
									}
								</div>

								<button>View on Github</button>
								<button>View site</button>
							</div>
						</div>
					)
				})
			}
			<button className="prevBtn" onClick={() => setCurrentProject(currentProject - 1)}>Prev</button>
			<button className="nextBtn" onClick={() => setCurrentProject(currentProject + 1)}>Next</button>
		</section>
	)
}

export default Projects
